                <Text style={styles.oddsValue}>{range.odds}x</Text>
              </View>

              <View style={styles.probabilityBarContainer}>
                <View 
                  style={[
                    styles.probabilityBar, 
                    { 
                      width: `${range.probability}%`,
                      backgroundColor: index === 0 
                        ? '#10B981' 
                        : index === 1 
                          ? '#3B82F6' 
                          : index === 2 
                            ? '#6366F1' 
                            : index === 3 
